import React from "react";
import { Tabs, Tab, Grid, Typography, Container, Box, Paper, Card, CardMedia, CardContent, CardActions, Button, AppBar } from '@material-ui/core'

import useStyles from "./styles";
import participants from "./data/Participants";

const ParticipantList = () => {

    const [value, setValue] = React.useState(0);
    const handleChange = (event, newValue) => {
      setValue(newValue);
    };
    return (
    <Grid container spacing={2} direction="row" >
            <Grid item xs={12}>
                <Container maxWidth="md">
                    <Typography variant="h2" align="center" color="textPrimary" gutterBottom>
                    ProMiSE participant overview
                    </Typography>
                    <Typography variant="h5"align="center"color="textSecondary" paragraph>
                    This website gives an overview of the different participants
                    </Typography>
                </Container>
            </Grid>
            <Grid item xs={2}>
                    <AppBar position="relative" color="transparent">
                        <Tabs
                        orientation="vertical"
                        variant="scrollable"
                        value={value}
                        onChange={handleChange}
                        aria-label="Vertical tabs example"
                        sx={{ borderRight: 1, borderColor: 'divider' }}
                        >
                        {participants.map((participant) => (
                            <Tab label={participant}/>
                        ))}
                        </Tabs>
                    </AppBar>
            </Grid>
            <Grid item xs={10}>
                {participants.map((participant, i) => (
                    <TabPanel value={value} index={i}>
                        {participant}
                    </TabPanel>
                ))}
            </Grid>
        </Grid>
    );
}

function TabPanel(props) {
    const classes = useStyles();
    const { children, value, index } = props;
    const [valueSubTab, setValue] = React.useState(0);
    const handleChange = (event, newValue) => {
        setValue(newValue);
      };
    return (
      <div>
        {value === index && (
        <Grid container spacing={2} justifyContent="center" alignItems="center">
            <Grid item xs={12}>
                <Box sx={{ width: '100%', bgcolor: 'background.paper' }}>
                    <Tabs value={valueSubTab} onChange={handleChange} centered>
                        <Tab label="Participant Overview"/>
                        <Tab label="User Story"/>
                    </Tabs>
                </Box>
            </Grid>
            <Grid item xs={10}>
                <Box sx={{ width: '100%', bgcolor: 'background.paper' }}>
                    <Card className={classes.card}>
                        <CardMedia 
                            className = {classes.cardMedia}
                            image={require('./data/'+String(children)+'.jpg')} 
                            title="Image title"
                        />
                        <CardContent className={classes.cardContent}>
                            <Typography variant="h5">
                                {children}
                            </Typography>
                            <Typography>
                                This image shows the user profil of {String(children).toLowerCase()}
                            </Typography>
                        </CardContent>
                    </Card>
                </Box>
            </Grid>
        </Grid>
        )}
      </div>
    );
  }

export default ParticipantList;
