import React from "react";
import { Typography, Button, Card, CardActions, CardContent, Divider, Grid, Container, CardMedia, Box, Modal } from '@material-ui/core'

import useStyles from "../styles";
import CssBaseline from '@mui/material/CssBaseline';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import VerticalSpace from "./subcomponents/VerticalSpace";
import {useState} from "react"


import participants from "../data/Participants";


const Tools = () => {
    const classes = useStyles()

    //added
    const [toolValue, setTool] = React.useState('');
    const [selectedParticipants, setSelectedParticipants] = React.useState(participants);

    const handleSelectedParticipants = (value) => {
        var selectedParticipants = []
        participants.map(participant => {
            if (participant.tool.includes(value)) {
                selectedParticipants.push(participant)
            }
        });
        if (value.target.value) {
            selectedParticipants = participants
        }
        setSelectedParticipants(selectedParticipants)
    }

    const handleChangeTool = (event) => {
      setTool(event.target.value);
      handleSelectedParticipants(event.target.value)
    };

    return (
            <Container maxWidth="lg">
                <CssBaseline />
                <Grid container spacing={2} direction="row">
                <VerticalSpace/>
                <VerticalSpace/>
                <Grid item xs={12}>
                    <Typography variant="h2" align="center" color="textPrimary">
                        ProMiSE tool data
                    </Typography>
                </Grid>
                <Grid item xs={3}></Grid>
                <Grid item xs={6}>
                    <Typography variant="h1" color="textPrimary" align="center">
                    For their analysis the participants use different tools. Here you find which tools participants used for the task.
                    </Typography>
                </Grid>
                <Grid item xs={3}></Grid>
                <VerticalSpace/>
                <Grid item xs={12}>
                    <Divider p={5}/>
                </Grid>
                <VerticalSpace/>
                <Grid item xs={12} align="center" variant="body1">
                    <Typography>
                        Select a tool from the list to filter the participants
                    </Typography>
                </Grid>
                <Grid item xs={12} align="center">
                <FormControl sx={{ m: 1, minWidth: 120 }} >
                    <CssBaseline />
                    <InputLabel id="demo-simple-select-helper-label">Tool</InputLabel>
                    <Select
                    labelId="demo-simple-select-helper-label"
                    id="demo-simple-select-helper"
                    value={toolValue}
                    label="Tool"
                    onChange={handleChangeTool}
                    >
                    <MenuItem value="None">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value={"Disco"}>Disco</MenuItem>
                    <MenuItem value={"Celonis"}>Celonis</MenuItem>
                    <MenuItem value={"BupaR"}>BupaR</MenuItem>
                    <MenuItem value={"Pm4Py"}>Pm4Py</MenuItem>
                    </Select>
                    <FormHelperText>Select Tool</FormHelperText>
                </FormControl> 
                </Grid>
                {toolValue != 'None' && toolValue != '' &&
                    <Grid container spacing={4}>
                        <Grid item xs={12}>
                            <Divider p={5}/>
                        </Grid>
                        <Grid item xs={12} sm={8} md={6}>
                            <Card className={classes.card}>
                                <CardMedia 
                                    className = {classes.cardMediaLarge}
                                    image={require('../data/tools/'+String(toolValue).toLowerCase()+'/'+String(toolValue).toLowerCase()+'_how.jpg')} 
                                />
                                <CardContent className={classes.cardContent} align="center">
                                    <Typography variant="h5">
                                        What data
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                        <Grid item xs={12} sm={8} md={6}>
                            <Card className={classes.card}>
                                <CardMedia 
                                    className = {classes.cardMediaLarge}
                                    image={require('../data/tools/'+String(toolValue).toLowerCase()+'/'+String(toolValue).toLowerCase()+'_what.jpg')} 
                                />
                                <CardContent className={classes.cardContent} align="center">
                                    <Typography variant="h5">
                                        How data
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                    </Grid>
                }
                <VerticalSpace/>
                <VerticalSpace/>
                <Grid item xs={12}>
                    <Divider p={5}/>
                </Grid>
                <VerticalSpace/>
                <VerticalSpace/>
                <Grid container spacing={4} style={{maxHeight: '100vh', overflow: 'hidden', overflowY: "scroll"}}>
                    {selectedParticipants.map((participant) => (   
                    <Grid item key={participant.name} xs={12} sm={6} md={4}>
                            <Card className={classes.card}>
                                <CardMedia 
                                    className = {classes.cardMedia}
                                    image={require('../data/participant_profiles/'+String(participant.name)+'_blank'+'.jpg')} 
                                />
                                <CardContent className={classes.cardContent}>
                                    <Typography variant="h5">
                                        {participant.name}
                                    </Typography>
                                    {participant.tool.map((tool,i) => (
                                        <Typography key={i}>
                                            Tool: {tool}
                                        </Typography>
                                        ))
                                    }
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
                <VerticalSpace/>
                <VerticalSpace/>
                <Grid item xs={12}>
                    <Divider p={5}/>
                </Grid>
                </Grid>
                <Grid item xs={12}>
                </Grid>
        </Container>
    );
}

export default Tools;
